// src/router/index.js - routes for project pages



import { createRouter, createWebHashHistory } from 'vue-router';
import Home from '@/views/Home.vue'
import ProjectItem from '@/components/ProjectItem.vue'

const routes = [
  { path: '/', name: 'Home', component: Home },
  { path: '/Projects/PrintCard',  name: 'PrintCard', component: ProjectItem, props: { projectCode: 'P0000', title: 'Print Card', description: 'Description for Print Card' } },
  { path: '/Projects/CardDesigner', name: 'CardDesigner', component: ProjectItem, props: { projectCode: 'P0001', title: 'Card Designer', description: 'Description for Card Designer' } },
  { path: '/Projects/PortfolioWebsite', name: 'PortfolioWebsite', component: ProjectItem, props: { projectCode: 'P0002', title: 'Portfolio Website', description: 'Description for Portfolio Website' } }
]

const router = createRouter({
  history: createWebHashHistory(),
  routes,
  scrollBehavior() {
    return { top: 0 }
  }
})

export default router
